// =================================================================================================
// Helper functions - for internal use, cannot be called by Bifrost directly.
// =================================================================================================

// Offsets for neighboring cells
const int3 UP = (int3)(0, 1, 0);
const int3 DOWN = (int3)(0, -1, 0);
const int3 LEFT = (int3)(-1, 0, 0);
const int3 RIGHT = (int3)(1, 0, 0);
const int3 FORWARD = (int3)(0, 0, 1);
const int3 BACKWARD = (int3)(0, 0, -1);
const int3 NEIGHBORS[6] = {UP, DOWN, LEFT, RIGHT, FORWARD, BACKWARD};

// Convert cell id to its world space position
float3 cellIdToPosition(uint cell_id, int3 global_size, float cell_size, float3 cell_offset) {
    int g_xy = global_size.x * global_size.y;
    int z = cell_id / g_xy;
    int y = (cell_id - z * g_xy) / global_size.x;
    int x = cell_id - z * g_xy - y * global_size.x;
    return (float3)(x * cell_size + cell_offset.x, y * cell_size + cell_offset.y, z * cell_size + cell_offset.z);
}

// Convert cell id to its coordinates in the grid
int3 cellIdCoords(uint cell_id, int3 global_size) {
    int g_xy = global_size.x * global_size.y;
    int z = cell_id / g_xy;
    int y = (cell_id - z * g_xy) / global_size.x;
    int x = cell_id - z * g_xy - y * global_size.x;
    return (int3)(x, y, z);
}

// Check if coordinates are within the grid
int coordsValid(int3 coords, int3 global_size) {
    return coords.x >= 0 && coords.x < global_size.x &&
           coords.y >= 0 && coords.y < global_size.y &&
           coords.z >= 0 && coords.z < global_size.z;
}


// Returns true of a cell is surrounded by other cells "in the set".
int isInternal(int3 coord, int3 global_size, float threshold, __global float* lengths) {
    for (int i = 0; i < 6; i++) {
        int3 neighbor = coord + NEIGHBORS[i];
        if (!coordsValid(neighbor, global_size)) {
            return 0;
        }

        int neighbor_id = neighbor.x + global_size.x * (neighbor.y + global_size.y * neighbor.z);
        if (!(lengths[neighbor_id] < threshold)) {
            return 0;
        }
    }

    return 1;
}

// -------------------------------------------------------------------------------------------------

// =================================================================================================
// Kernels - functions that can be called in Bifrost via `execute_kernel`
// =================================================================================================

// Main compute kernel
// =================================================================================================
// computes if a given position is in the Julia set
// idk if I implemented the equation correctly for 3d, but looks cool enough.
__kernel void sampleJulia(
    // Inputs
    float cell_size,    // size of each cell.
    float3 cell_offset, // position of the first cell in world space.
    int3 global_size,   // dimensions of the grid.
    int iterations,     // number of iterations to run the Julia set calculation.
    float4 parameters,  // Julia set params.

    // Outputs
    __global float* lengths
) {
    // prep params for loop
    uint cell_id = get_global_id(0);
    float3 position = cellIdToPosition(cell_id, global_size, cell_size, cell_offset);
    float x = position.x;
    float3 yzw = (float3)(position.y, position.z, 0);
    const float param_x = parameters.x;
    const float3 param_yzw = (float3)(parameters.y, parameters.z, parameters.w);

    // compute
    float x2;
    for (int i = 0; i < iterations; i++) {
        x2 = x*2;  // we'll need this for updating yzw, so store before changing x
        x = x*x - dot(yzw, yzw) + parameters.x;  // new_x = x^2 - yzw.length^2 + parameter.x
        yzw = yzw*x2 + param_yzw;
    }

    // output resulting vector length
    lengths[cell_id] = length((float3)(x, yzw.x, yzw.y));
}


// Find All True
// =================================================================================================
// This performs the essentially the same function as bifrost's filter_array. However we do this
// on the GPU, not just for computation speed, but to limit memory IO.
// 
// Since we can't know the size of the output array in advance, size of `true_indices` is equal to size of `lengths`.
// We fill `true_indices` as we go, and in the end `true_count` will tell us how much of the array is used.
// We then read back into bifrost only that portion of the array.
__kernel void findAllTrue(
    __global float* lengths,    // Lengths from the previous kernel
    const float threshold,      // Lengths < threshold are considered "in the set"
    __global int* true_indices, // Output array of indices of cells that are in the set
    __global int* true_count,   // Output number of cells that are in the set
    const int3 global_size      // Dimensions of the grid
) {
    int gid = get_global_id(0);
    const float local_len = lengths[gid];

    if (!(lengths[gid] < threshold))
        return;  // Not in the set

    int3 coord = cellIdCoords(gid, global_size);

    if (isInternal(coord, global_size, threshold, lengths)) {
        // Optional step to cull internal cells. This increases kernel compute time,
        // but means fewer points that bifrost has to create, which will generally be of major net benefit.
        return;
    }

    // If we get here we're the chosen ones. Each thread increments true_count,
    // which also returns the next available index in true_indices.
    int index = atomic_inc(true_count);
    true_indices[index] = gid;

    barrier(CLK_GLOBAL_MEM_FENCE);  // Verify all threads have finished reading `lengths` before we modify it.
    lengths[index] = local_len;  // reorder lengths to align with true_indices
}


// Compute final positions
// =================================================================================================
// given the filtered cell indices, compute their world positions.
// We already did this for every cell in the first kernel, and could've just stored those in a buffer for reuse.
// However that would dramatically increase memory usage. This way we only store points we know we'll need.
__kernel void finalPositions(
    // Inputs
    __global const int* indices,
    const float cell_size,
    const float3 cell_offset,
    const int3 global_size,

    // Outputs
    __global float3* positions
) {
    int gid = get_global_id(0);
    positions[gid] = cellIdToPosition(indices[gid], global_size, cell_size, cell_offset);
}
