inline float4 lerp(float4 a, float4 b, float t) {
    return a + t * (b - a);
}


__kernel void doit(
    __read_only image2d_t input_image,
    __read_write image2d_t output_image,
    __read_only image2d_t weights_image,
    sampler_t sampler,
    int2 extents,
    __global float* kernel_weights
) {
    const uint x = get_global_id(0);
    const uint y = get_global_id(1);
    const int2 coord = (int2)(x, y);
    const uint local_size = get_local_size(0);
    const float blend_weight = read_imagef(weights_image, sampler, coord).x;

    int w_index = 0;
    float4 sum = (float4)(0.0f);
    for (int ky = -extents.y; ky <= extents.y; ky++) {
        for (int kx = -extents.x; kx <= extents.x; kx++) {
            int target_x = x + kx;
            int target_y = y + ky;
            int2 target_coord = (int2)(target_x, target_y);

            float w = kernel_weights[w_index++];

            float4 pixel = read_imagef(input_image, sampler, target_coord);
            sum += pixel * w;
        }
    }
    sum = lerp(read_imagef(input_image, coord), sum, blend_weight);
    write_imagef(output_image, coord, sum);
}
