const sampler_t sampler = CLK_NORMALIZED_COORDS_FALSE | CLK_ADDRESS_CLAMP_TO_EDGE | CLK_FILTER_NEAREST;

inline float lerp(float a, float b, float t) {
    return a + t * (b - a);
}


__kernel void filter_masked(
    __read_only image2d_t input_image,
    __read_write image2d_t output_image,
    int2 extents,
    __global float* kernel_weights,
    __read_only image2d_t mask
) {
    const uint x = get_global_id(0);
    const uint y = get_global_id(1);
    const int2 coord = (int2)(x, y);
    const uint local_size = get_local_size(0);
    const float m = read_imagef(mask, sampler, coord).x;

    int w_index = 0;
    float sum = 0.0f;
    for (int ky = -extents.y; ky <= extents.y; ky++) {
        for (int kx = -extents.x; kx <= extents.x; kx++) {
            int target_x = x + kx;
            int target_y = y + ky;
            int2 target_coord = (int2)(target_x, target_y);

            float w = kernel_weights[w_index++];

            float pixel = read_imagef(input_image, sampler, target_coord).x;
            sum += pixel * w;
        }
    }
    sum = lerp(read_imagef(input_image, coord).x, sum, m);
    write_imagef(output_image, coord, sum);
}


__kernel void filter(
    __read_only image2d_t input_image,
    __read_write image2d_t output_image,
    int2 extents,
    __global float* kernel_weights
) {
    const uint x = get_global_id(0);
    const uint y = get_global_id(1);
    const int2 coord = (int2)(x, y);
    const uint local_size = get_local_size(0);

    int w_index = 0;
    float sum = 0.0f;
    for (int ky = -extents.y; ky <= extents.y; ky++) {
        for (int kx = -extents.x; kx <= extents.x; kx++) {
            int target_x = x + kx;
            int target_y = y + ky;
            int2 target_coord = (int2)(target_x, target_y);

            float w = kernel_weights[w_index++];

            float pixel = read_imagef(input_image, sampler, target_coord).x;
            sum += pixel * w;
        }
    }
    write_imagef(output_image, coord, sum);
}
