typedef union {
    float4 vec;
    struct {
        float height;
        float water;
        float sediment;
        float solubility;  // 0-1 used as multiplier for dissolve
    };
} CellState_t;

__constant sampler_t NST_SAMPLER = CLK_NORMALIZED_COORDS_FALSE | CLK_ADDRESS_CLAMP | CLK_FILTER_NEAREST;
__constant sampler_t THERMAL_SAMPLER = CLK_NORMALIZED_COORDS_FALSE | CLK_ADDRESS_CLAMP_TO_EDGE | CLK_FILTER_NEAREST;
__constant sampler_t RAIN_SAMPLER = CLK_NORMALIZED_COORDS_FALSE | CLK_ADDRESS_CLAMP_TO_EDGE | CLK_FILTER_NEAREST;

__constant float CapacityScale = 10.5f;
__constant float DissolveRate = 0.01f;
__constant float Gravity = 0.3f;
__constant float HardenRate = 1.f;
__constant float SoftenRate = 1.f;
__constant float FlowRate = 1.0f;  // how fast water can leave a cell, 1 means all water can leave in a single step
__constant float DepthMax = 500.f;  // max depth where sediment can be dissolved
// __constant float Evaporation = 0.001f;  // precentage of water removed each step
__constant float DrySolubility = 0.9f;
__constant float DryingRate = 0.1f;
__constant float WaterInertia = 0.5f;


typedef struct {
    struct {
        float RainScale;
        float DissolveRate;
        float HardenRate;
        float CapacityScale;
    };
} Step1Params_t;

typedef struct {
    struct {
        float Gravity;
        float FlowRate;
        float Inertia;
        float pad1;
    };
} Step2Params_t;

typedef struct {
    struct {
        float CapacityScale;
        float SoftenRate;
        float DrySolubility;
        float DryingRate;
    };
} Step3Params_t;

typedef struct {
    struct {
        float Rate;
        float ThesholdStart;
        float ThesholdEnd;
        float pad1;
    };
} ThermalParams_t;

inline float waterCapacity(float water) {
    float factor = water / DepthMax;
    // return water * (1.0 - clamp(factor, 0.f, 1.f)) * (4.f/DepthMax);
    return (1.0 - clamp(factor, 0.f, 1.f));
}

inline float stateHeight(CellState_t State) {
    return State.height + State.water + State.sediment;
}

inline float safeDivide(float nom, float dom) {
    return select(0.f, nom / dom, dom > 0);
}

inline float vectorSum(float4 v) {
    return v.x + v.y + v.z + v.w;
}

float4 updateOutflow(float Height, float4 NeighborHeight, float4 Outflow, float _gravity, float inertia) {
    float4 Delta = Height - NeighborHeight;
    Outflow = Outflow + mix(Delta * _gravity, Outflow, inertia);

    // this cell only solves for water that leaves, inflow is determined in the
    // next step by summing its neighbor's outflow in the direction of this cell
    return max(Outflow, 0.f);
}

__kernel void step1(  // rain and dissolve
    __read_only image2d_t riRain,
    __read_only image2d_t riCellState,
    __write_only image2d_t wiCellState,
    __read_only image2d_t riVelocity,
    __read_only image2d_t riMaterial,
    __write_only image2d_t wiMaterial,
    const Step1Params_t Param
) {
    int2 coord = (int2)(get_global_id(0), get_global_id(1));
    CellState_t State;
    State.vec = read_imagef(riCellState, coord);
    float2 vel = read_imagef(riVelocity, coord).xy;
    float Rain = read_imagef(riRain, RAIN_SAMPLER, coord).x;

    // Add water
    State.water += Rain * Param.RainScale;
    State.water = max(0.f, State.water);

    // Dissolve Sediment
    float Capacity = min(Param.CapacityScale * length(vel) * waterCapacity(State.water), State.water);
    float FreeCapacity = max(0.f, Capacity - State.sediment);
    float Dissolved = State.solubility * DissolveRate * FreeCapacity;
    Dissolved = min(Dissolved, FreeCapacity);
    State.height -= Dissolved;
    State.sediment += Dissolved;
    State.solubility -= Dissolved * Param.HardenRate;
    State.solubility = clamp(State.solubility, 0.f, 1.f);

    write_imagef(wiCellState, coord, State.vec);

    float4 material = read_imagef(riMaterial, coord);
    material.x += Dissolved;
    write_imagef(wiMaterial, coord, material);
}


__kernel void step2(  // compute outflow
    __read_only image2d_t riCellState,
    __read_only image2d_t riOutflow,
    __write_only image2d_t wiOutflow,
    const Step2Params_t Param
) {
    int2 coord = (int2)(get_global_id(0), get_global_id(1));
    CellState_t State;
    State.vec = read_imagef(riCellState, coord);
    float4 Outflow = read_imagef(riOutflow, coord);

    float Height = stateHeight(State);
    float4 NeighborHeight;
    CellState_t NeighborState;

    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x, coord.y+1));
    NeighborHeight.x = stateHeight(NeighborState);
    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x+1, coord.y));
    NeighborHeight.y = stateHeight(NeighborState);
    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x, coord.y-1));
    NeighborHeight.z = stateHeight(NeighborState);
    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x-1, coord.y));
    NeighborHeight.w = stateHeight(NeighborState);

    Outflow = updateOutflow(Height, NeighborHeight, Outflow, Param.Gravity, Param.Inertia);
    float FlowSum = vectorSum(Outflow);
    // scale outflow if FlowSum > water, select used to avoid div by 0
    Outflow *= min(safeDivide((State.water +  State.sediment), FlowSum), 1.f) * Param.FlowRate;

    write_imagef(wiOutflow, coord, Outflow);
}


__kernel void step3(  // apply flow and depossite
    __read_only image2d_t riCellState,
    __write_only image2d_t wiCellState,
    __read_only image2d_t riOutflow,
    __write_only image2d_t wiVelocity,
    __read_only image2d_t riMaterial,
    __write_only image2d_t wiMaterial,
    const Step3Params_t Param,
    const float2 Evaporation
) {
    int2 coord = (int2)(get_global_id(0), get_global_id(1));
    CellState_t State;
    State.vec = read_imagef(riCellState, coord);

    float4 Outflow = read_imagef(riOutflow, coord);
    float4 Inflow;
    Inflow.x = read_imagef(riOutflow, NST_SAMPLER, (int2)(coord.x, coord.y+1)).z;
    Inflow.y = read_imagef(riOutflow, NST_SAMPLER, (int2)(coord.x+1, coord.y)).w;
    Inflow.z = read_imagef(riOutflow, NST_SAMPLER, (int2)(coord.x, coord.y-1)).x;
    Inflow.w = read_imagef(riOutflow, NST_SAMPLER, (int2)(coord.x-1, coord.y)).y;

    // Remove sed from outflow and take neighbor sed proportional to inflow
    float Percent = safeDivide(vectorSum(Outflow), State.water + State.sediment);
    State.water -= State.water * Percent;
    State.sediment -= State.sediment * Percent;
    CellState_t NeighborState;

    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x, coord.y+1));
    Percent = safeDivide(Inflow.x, NeighborState.water + NeighborState.sediment);
    State.water += NeighborState.water * Percent;
    State.sediment += NeighborState.sediment * Percent;
    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x+1, coord.y));
    Percent = safeDivide(Inflow.y, NeighborState.water + NeighborState.sediment);
    State.water += NeighborState.water * Percent;
    State.sediment += NeighborState.sediment * Percent;
    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x, coord.y-1));
    Percent = safeDivide(Inflow.z, NeighborState.water + NeighborState.sediment);
    State.water += NeighborState.water * Percent;
    State.sediment += NeighborState.sediment * Percent;
    NeighborState.vec = read_imagef(riCellState, NST_SAMPLER, (int2)(coord.x-1, coord.y));
    Percent = safeDivide(Inflow.w, NeighborState.water + NeighborState.sediment);
    State.water += NeighborState.water * Percent;
    State.sediment += NeighborState.sediment * Percent;

    // Update velocity
    float4 vel;
    vel.x = (Inflow.w - Outflow.w) + (Outflow.y - Inflow.y);
    vel.y = (Inflow.x - Outflow.x) + (Outflow.z - Inflow.z);
    vel *= .5f;

    write_imagef(wiVelocity, coord, vel);

    State.water *= 1.f - Evaporation.y;
    State.water = max(State.water - Evaporation.x, 0.f);
    float Capacity = min(Param.CapacityScale * length(vel) * waterCapacity(State.water), State.water);
    // float Capacity = CapacityScale * waterCapacity(State.water);
    // float Deposite = max(0.f, State.sediment-Capacity);
    float Deposite = max(State.sediment * 0.0001f, State.sediment-Capacity);
    State.height += Deposite;
    State.sediment -= Deposite;
    State.solubility += Deposite * Param.SoftenRate;
    State.solubility = clamp(State.solubility, 0.f, 1.f);

    // Apply Drying
    float deltaSol = Param.DrySolubility - State.solubility;
    State.solubility += select(max(deltaSol, -Param.DryingRate), min(deltaSol, Param.DryingRate), deltaSol > 0.f);

    write_imagef(wiCellState, coord, State.vec);

    float4 material = read_imagef(riMaterial, coord);
    material.y += Deposite;
    write_imagef(wiMaterial, coord, material);
}


__kernel void final_height(  // apply flow and depossite
    __read_only image2d_t _riHeight,
    __write_only image2d_t wiOut,
    __read_only image2d_t riCellState,
    const float2 WaterScale
) {
    int2 coord = (int2)(get_global_id(0), get_global_id(1));
    CellState_t State;
    State.vec = read_imagef(riCellState, coord);

    write_imagef(wiOut, coord, State.height + (State.water * WaterScale.x) + (State.sediment * WaterScale.y));
}


// THERMAL ERSION ///////////////////////////////////////////////////////////////////

__kernel void thermal_erosion_compute(
    __read_only image2d_t riCellState,
    __write_only image2d_t wiSlide,
    __write_only image2d_t wiSlideDiag,
    __read_only image2d_t riThresholdMap,
    const ThermalParams_t Param
) {
    int2 coord = (int2)(get_global_id(0), get_global_id(1));
    CellState_t State;
    State.vec = read_imagef(riCellState, coord);

    float alpha = read_imagef(riThresholdMap, THERMAL_SAMPLER, coord).x;
    float thresh = mix(Param.ThesholdStart, Param.ThesholdEnd, alpha);

    float4 NeighborHeight;
    float4 NeighborHeightDiag;
    CellState_t NeighborState;

    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x, coord.y+1));
    NeighborHeight.x = NeighborState.height;
    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x+1, coord.y));
    NeighborHeight.y = NeighborState.height;
    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x, coord.y-1));
    NeighborHeight.z = NeighborState.height;
    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x-1, coord.y));
    NeighborHeight.w = NeighborState.height;

    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x+1, coord.y+1));
    NeighborHeightDiag.x = NeighborState.height;
    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x+1, coord.y-1));
    NeighborHeightDiag.y = NeighborState.height;
    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x-1, coord.y-1));
    NeighborHeightDiag.z = NeighborState.height;
    NeighborState.vec = read_imagef(riCellState, THERMAL_SAMPLER, (int2)(coord.x-1, coord.y+1));
    NeighborHeightDiag.w = NeighborState.height;

    // This follows how the algorithm is typically described however I feel like
    // UnstableHeight soulc be only what's over threshold: (State.height - threshold) - NeighborHeight
    float4 UnstableHeight = State.height - NeighborHeight;
    float4 UnstableHeightDiag = State.height - NeighborHeightDiag;
    UnstableHeight = select(0.f, UnstableHeight, isgreater(UnstableHeight, thresh));
    UnstableHeightDiag = select(0.f, UnstableHeightDiag, isgreater(UnstableHeightDiag, thresh));

    float sum = vectorSum(UnstableHeight) + vectorSum(UnstableHeightDiag);
    float factor = sum / Param.Rate;
    factor = max(factor, 1.f);
    UnstableHeight /= factor;
    UnstableHeightDiag /= factor;


    write_imagef(wiSlide, coord, UnstableHeight);
    write_imagef(wiSlideDiag, coord, UnstableHeightDiag);

}

__kernel void thermal_erosion_apply(
    __read_only image2d_t riCellState,
    __write_only image2d_t wiCellState,
    __read_only image2d_t riSlide,
    __read_only image2d_t riSlideDiag,
    __read_only image2d_t riMaterial,
    __write_only image2d_t wiMaterial
) {
    int2 coord = (int2)(get_global_id(0), get_global_id(1));
    CellState_t State;
    State.vec = read_imagef(riCellState, coord);

    float release = vectorSum(read_imagef(riSlide, coord)) + vectorSum(read_imagef(riSlideDiag, coord));
    float accum = 0.f;
    accum += read_imagef(riSlide, NST_SAMPLER, (int2)(coord.x, coord.y+1)).z;
    accum += read_imagef(riSlide, NST_SAMPLER, (int2)(coord.x+1, coord.y)).w;
    accum += read_imagef(riSlide, NST_SAMPLER, (int2)(coord.x, coord.y-1)).x;
    accum += read_imagef(riSlide, NST_SAMPLER, (int2)(coord.x-1, coord.y)).y;
    accum += read_imagef(riSlideDiag, NST_SAMPLER, (int2)(coord.x+1, coord.y+1)).z;
    accum += read_imagef(riSlideDiag, NST_SAMPLER, (int2)(coord.x+1, coord.y-1)).w;
    accum += read_imagef(riSlideDiag, NST_SAMPLER, (int2)(coord.x-1, coord.y-1)).x;
    accum += read_imagef(riSlideDiag, NST_SAMPLER, (int2)(coord.x-1, coord.y+1)).y;

    State.height -= release;
    State.height += accum;

    write_imagef(wiCellState, coord, State.vec);

    float4 material = read_imagef(riMaterial, coord);
    material.z += release;
    material.w += accum;
    write_imagef(wiMaterial, coord, material);
}


__kernel void apply_detail_restore(
    __read_only image2d_t riHeightOut,
    __write_only image2d_t wiHeightOut,
    __read_only image2d_t riOrigHeight,
    __read_only image2d_t riBlurHeight,
    __read_only image2d_t riCellState,
    const float DetailMag,
    const float WaterMaskScale
) {
    int2 coord = (int2)(get_global_id(0), get_global_id(1));
    float h = read_imagef(riHeightOut, coord).x;
    float oh = read_imagef(riOrigHeight, coord).x;
    float bh = read_imagef(riBlurHeight, coord).x;
    CellState_t State;
    State.vec = read_imagef(riCellState, coord);

    float hOut = h + DetailMag * (oh - bh);
    float m = clamp(State.water * WaterMaskScale, 0.f, 1.f);
    hOut = mix(hOut, h, m);
    write_imagef(wiHeightOut, coord, hOut);
}
