float changeRange(float v, float old_min, float old_max, float new_min, float new_max) {
    float slope = (new_max - new_min) / (old_max - old_min);
    return new_min + slope * (v - old_min);
}

float changeRangeClamped(float v, float old_min, float old_max, float new_min, float new_max) {
    float slope = (new_max - new_min) / (old_max - old_min);
    float new_v = new_min + slope * (v - old_min);
    return clamp(new_v, new_min, new_max);
}


