__constant float3 GRADIENTS[16] = {
        (float3)(1.f, 1.f, 1.f), (float3)(-1.f, 1.f, -1.f), (float3)(1.f, -1.f, 1.f), (float3)(-1.f, -1.f, -1.f),
        (float3)(1.f, -1.f, 1.f), (float3)(-1.f, -1.f, 1.f), (float3)(1.f, 1.f, -1.f), (float3)(-1.f, -1.f, -1.f),
        (float3)(-1.f, 1.f, 1.f), (float3)(1.f, -1.f, 1.f), (float3)(-1.f, 1.f, -1.f), (float3)(1.f, -1.f, -1.f),
        (float3)(1.f, 1.f, -1.f), (float3)(-1.f, 1.f, -1.f), (float3)(1.f, -1.f, 1.f), (float3)(-1.f, -1.f, -1.f)
    };

const sampler_t UV_SAMPLER = CLK_NORMALIZED_COORDS_TRUE | CLK_ADDRESS_CLAMP_TO_EDGE | CLK_FILTER_LINEAR;

uint hash(uint x) {  // Jenkins hash
    x += (x << 10);
    x ^= (x >> 6);
    x += (x << 3);
    x ^= (x >> 11);
    x += (x << 15);
    return x;
}

// Hash for combining multiple integers
uint hash_coords(int x, int y, int z, uint seed) {
    uint h = seed;
    h = hash(h + (uint)x);
    h = hash(h + (uint)y);
    h = hash(h + (uint)z);
    return h;
}

float3 gradient_from_hash(uint hash) {
    // Use all 4 bits to generate a unique gradient
    uint h = hash & 15;
    return GRADIENTS[h];
}

float dot_grid_gradient(int ix, int iy, int iz, float x, float y, float z, uint seed) {
    // Get gradient vector from hash
    uint hash_value = hash_coords(ix, iy, iz, seed);
    float3 gradient = gradient_from_hash(hash_value);

    // Compute the distance vector
    float dx = x - (float)ix;
    float dy = y - (float)iy;
    float dz = z - (float)iz;

    // Compute dot product
    return (dx * gradient.x + dy * gradient.y + dz * gradient.z);
}

float fade(float t) {
    return t * t * t * (t * (t * 6.0f - 15.0f) + 10.0f);
}

float lerp(float t, float a, float b) {
    return a + t * (b - a);
}

float noise(float x, float y, float z, uint seed) {
    // Get integer coordinates
    int x0 = (int)floor(x);
    int y0 = (int)floor(y);
    int z0 = (int)floor(z);

    // Get fractional coordinates
    float xf = x - x0;
    float yf = y - y0;
    float zf = z - z0;

    // Compute fade curves
    float u = fade(xf);
    float v = fade(yf);
    float w = fade(zf);

    // Calculate dot products for all corners
    float n000 = dot_grid_gradient(x0, y0, z0, x, y, z, seed);
    float n001 = dot_grid_gradient(x0, y0, z0+1, x, y, z, seed);
    float n010 = dot_grid_gradient(x0, y0+1, z0, x, y, z, seed);
    float n011 = dot_grid_gradient(x0, y0+1, z0+1, x, y, z, seed);
    float n100 = dot_grid_gradient(x0+1, y0, z0, x, y, z, seed);
    float n101 = dot_grid_gradient(x0+1, y0, z0+1, x, y, z, seed);
    float n110 = dot_grid_gradient(x0+1, y0+1, z0, x, y, z, seed);
    float n111 = dot_grid_gradient(x0+1, y0+1, z0+1, x, y, z, seed);

    // Interpolate along x
    float nx00 = lerp(u, n000, n100);
    float nx01 = lerp(u, n001, n101);
    float nx10 = lerp(u, n010, n110);
    float nx11 = lerp(u, n011, n111);

    // Interpolate along y
    float nxy0 = lerp(v, nx00, nx10);
    float nxy1 = lerp(v, nx01, nx11);

    // Interpolate along z
    return lerp(w, nxy0, nxy1);
}


uint shuffleSeed(uint seed, uint i) {
    // return (seed * 1664525 + 1013904223) % 0xFFFFFFFF;
    return seed ^ (uint)(i * 1099511627776UL);
}


inline float3 transform_point(float3 point, float16 m) {
    float4 v = (float4)(point.x, point.y, point.z, 1.0f);
    float3 r;
    r.x = dot(v, m.s048C); // Row 0
    r.y = dot(v, m.s159D); // Row 1
    r.z = dot(v, m.s26AE);
    return r;
}


typedef struct {
    float h;    // height
    float e;    // extent
    float s;    // slope
    float exp;   // p.w (if not used)
} plateau_t;


__kernel void doit(
    __read_only image2d_t image_in,
    __write_only image2d_t image_out,
    __constant plateau_t *plateaus,
    const uint plateaus_size,
    __write_only image2d_t mask_out
) {
    int2 icoord = (int2)(get_global_id(0), get_global_id(1));
    float h = read_imagef(image_in, icoord).x;
    float mo = 0.f;
    bool write_mask = get_image_width(mask_out) > 1;

    for (uint i = 0; i < plateaus_size; ++i) {
        plateau_t p = plateaus[i];  // plateau height, extent, slope
        bool m_under = h <= p.h;
        bool m_in = h > p.h && (h < p.h + p.e);
        bool m_over = !(m_under || m_in);

        float f = (h - p.h) / p.e;
        f = clamp(f, 0.f, 1.f);
        mo = max(mo, (1.f-f)*m_in);
        f = pow(f, p.exp);
        f*=p.s;

        float h_in = (h-p.h) * f + p.h;
        float h_over = h - (1.f-p.s) * p.e;
        h = h * m_under + h_in * m_in + h_over * m_over;

    }

    write_imagef(image_out, icoord, h);
    if (write_mask) {
        write_imagef(mask_out, icoord, mo);
    }
}
