bool bounds_dont_intersect(float4 a, float4 b) {
    return (a.x > b.z || a.z < b.x || a.y > b.w || a.w < b.y);
}

bool point_out_of_bounds(float4 bounds, float2 point) {
    return (point.x < bounds.x || point.x > bounds.z || point.y < bounds.y || point.y > bounds.w);
}

inline void atomic_maxf(volatile __global float *g_val, float myValue) {
    float oldValue, newValue;
    int *intAddr = (int *)g_val;
    int assumed;

    do {
        assumed = atomic_load_explicit((volatile __global atomic_int *)intAddr, memory_order_relaxed);
        oldValue = as_float(assumed);
        newValue = max(oldValue, myValue);

        if (oldValue == newValue) {
            break;  // Skip if no change needed
        }
    } while (atomic_cmpxchg((volatile __global atomic_int *)intAddr,
                           assumed,
                           as_int(newValue)) != assumed);
}


inline void atomic_minf(volatile __global float *g_val, float myValue) {
    float oldValue, newValue;
    int *intAddr = (int *)g_val;
    int assumed;

    do {
        assumed = atomic_load_explicit((volatile __global atomic_int *)intAddr, memory_order_relaxed);
        oldValue = as_float(assumed);
        newValue = min(oldValue, myValue);

        if (oldValue == newValue) {
            break;  // Skip if no change needed
        }
    } while (atomic_cmpxchg((volatile __global atomic_int *)intAddr,
                           assumed,
                           as_int(newValue)) != assumed);
}


__kernel void compute_bounding_boxes(
    __global const float3* points,
    __global const uint* triangles,
    __global float4* tri_bounds,
    __global float* global_bounds  // Changed to float array [minX, minZ, maxX, maxZ]
) {
    int idx = get_global_id(0);
    uint base = idx * 3;

    // Compute per-triangle bounds
    float3 v1 = points[triangles[base]];
    float3 v2 = points[triangles[base + 1]];
    float3 v3 = points[triangles[base + 2]];

    float minX = fmin(fmin(v1.x, v2.x), v3.x);
    float minZ = fmin(fmin(v1.z, v2.z), v3.z);
    float maxX = fmax(fmax(v1.x, v2.x), v3.x);
    float maxZ = fmax(fmax(v1.z, v2.z), v3.z);

    // Store triangle bounds
    tri_bounds[idx] = (float4)(minX, minZ, maxX, maxZ);

    // Atomically update global bounds using separate float variables
    atomic_minf(&global_bounds[0], minX);
    atomic_minf(&global_bounds[1], minZ);
    atomic_maxf(&global_bounds[2], maxX);
    atomic_maxf(&global_bounds[3], maxZ);
}


__kernel void mesh_to_height(
    __read_only image2d_t height_in,
    __write_only image2d_t height_out,
    const float2 corner,
    const float2 cell_size,
    __global const float3* points,
    __global const uint* triangles,
    const uint num_triangles,
    __global const float4* tri_bounds,
    const float4 global_bounds,
    __local uint* local_tri_ids,
    __local uint* local_tri_count,
    const uint max_local_tri,
    const uchar project_up
) {
    int2 gid = (int2)(get_global_id(0), get_global_id(1));
    int2 lid = (int2)(get_local_id(0), get_local_id(1));
    int2 local_size = (int2)(get_local_size(0), get_local_size(1));
    int local_index = lid.x + lid.y * local_size.x;
    bool im_in_image = gid.x < get_image_width(height_out) && gid.y < get_image_height(height_out);

    // Calculate world space position
    float x = corner.x + gid.x * cell_size.x;
    float z = corner.y + gid.y * cell_size.y;
    float2 point = (float2)(x, z);

    // Local bounds
    float4 local_bounds;  // [x_min, z_min, x_max, z_max]
    local_bounds.x = corner.x + (gid.x-lid.x) * cell_size.x;
    local_bounds.w = corner.y + (gid.y-lid.y) * cell_size.y;
    local_bounds.z = local_bounds.x + cell_size.x * local_size.x;
    local_bounds.y = local_bounds.w + cell_size.y * local_size.y;

    // Check if local tile is outside mesh bounds
    if (bounds_dont_intersect(local_bounds, global_bounds)) {
        if (!im_in_image) { return; }
        float default_height = read_imagef(height_in, gid).x;
        write_imagef(height_out, gid, default_height);
        return;
    }

    if (local_index == 0) { *local_tri_count = 0; }  // Initialize local triangle count
    barrier(CLK_LOCAL_MEM_FENCE);

    uint index = 0;
    for (uint i = local_index; i < num_triangles; i += local_size.x * local_size.y) {
        float4 tri_bb = tri_bounds[i];
        if (bounds_dont_intersect(tri_bb, local_bounds)) {
            continue;  // Skip if triangle bounding box is outside local bounds
        }
        index = atomic_inc(local_tri_count);
        if (index >= max_local_tri) {
            break;
        }
        local_tri_ids[index] = i;  // Store triangle ID in local memory
    }

    // Return if work item is out of image bounds
    // We need the work item to help load triangles in tile, but now it can return.
    if (!im_in_image) { return; }
    barrier(CLK_LOCAL_MEM_FENCE);

    float max_hit = read_imagef(height_in, gid).x;  // Get default height from image
    float h0 = max_hit;
    // Return if pixel out of global bounds
    if (point_out_of_bounds(global_bounds, point) || *local_tri_count == 0) {
        write_imagef(height_out, gid, max_hit);
        return;
    }

    // For each triangle
    uint tri_count = min(*local_tri_count, max_local_tri);  // in case overflow
    for (int i = 0; i < tri_count; i++) {
        uint trid = local_tri_ids[i];

        float4 bbox = tri_bounds[trid];
        if (point_out_of_bounds(bbox, point)) {
            continue;  // Skip if point is outside triangle's bounding box
        }

        // Get triangle vertices
        uint tri_start = trid * 3;
        uint idx1 = triangles[tri_start];
        uint idx2 = triangles[tri_start + 1];
        uint idx3 = triangles[tri_start + 2];

        float3 v1 = points[idx1];
        float3 v2 = points[idx2];
        float3 v3 = points[idx3];

        // Check if point (x,z) is inside triangle projection
        float2 p = (float2)(x, z);
        float2 a = (float2)(v1.x, v1.z);
        float2 b = (float2)(v2.x, v2.z);
        float2 c = (float2)(v3.x, v3.z);

        // Calculate barycentric coordinates
        float2 v0 = b - a;
        float2 v1_b = c - a;
        float2 v2_b = p - a;

        float d00 = dot(v0, v0);
        float d01 = dot(v0, v1_b);
        float d11 = dot(v1_b, v1_b);
        float d20 = dot(v2_b, v0);
        float d21 = dot(v2_b, v1_b);

        float denom = d00 * d11 - d01 * d01;
        if (fabs(denom) < 1e-6f) continue;  // Skip degenerate triangles

        float v = (d11 * d20 - d01 * d21) / denom;
        float w = (d00 * d21 - d01 * d20) / denom;
        float u = 1.0f - v - w;

        // If point is inside triangle
        if (u >= 0.0f && v >= 0.0f && w >= 0.0f) {
            float height = u * v1.y + v * v2.y + w * v3.y;
            max_hit = project_up ? fmin(max_hit, height) : fmax(max_hit, height);
        }
    }
    write_imagef(height_out, gid, max_hit);
}

__kernel void create_mask(
    __read_only image2d_t height,
    __read_only image2d_t height0,
    __write_only image2d_t mask_out,
    const uint mode
) {
    int2 icoord = (int2)(get_global_id(0), get_global_id(1));
    float h = read_imagef(height, icoord).x;
    float h0 = read_imagef(height0, icoord).x;

    float delta = h-h0;
    float out;

    switch (mode) {
    case 1: // hit
        out = fabs(delta) > FLT_EPSILON;
        break;
    case 2:  // delta signed
        out = delta;
        break;
    case 3:  // delta unsigned
        out = fabs(delta);
        break;
    default:
        out = 1.f;
        break;
}

    write_imagef(mask_out, icoord, out);
}
