__kernel void resize_image(
    __read_only image2d_t original,
    __write_only image2d_t resized,
    const sampler_t sampler

) {
    int ix = get_global_id(0);
    int iy = get_global_id(1);
    float x = (float)ix / (get_global_size(0)-1);
    float y = (float)iy / (get_global_size(1)-1);
    x*=get_image_width(original)-1;
    y*=get_image_height(original)-1;
    // for two images of the same size, x and y will equal ix and iy.
    // however for a 1:1 image with linear interp you need to sample the 'middle' of the pixel
    // hence the +0.5f
    float4 value = read_imagef(original, sampler, (float2)(x, y) + 0.5f);
    write_imagef(resized, (int2)(ix, iy), value);
}
